import React, { Component } from 'react';
import Container from '../container';
import NavBar from '../navbar';
import Header from '../header';
import Footer from '../footer';

class MainContainer extends Component {
    state = {
        score: 0,
        topScore: 0,
        msg:'',
        items: {},
        images: [
            {src:'/assets/images/1.jpeg',name:'1'},
            {src:'/assets/images/2.jpeg',name:'2'},
            {src:'/assets/images/3.jpeg',name:'3'},
            {src:'/assets/images/4.jpeg',name:'4'},
            {src:'/assets/images/5.jpeg',name:'5'},
            {src:'/assets/images/6.jpeg',name:'6'},
            {src:'/assets/images/7.jpeg',name:'7'},
            {src:'/assets/images/8.jpeg',name:'8'},
            {src:'/assets/images/9.jpeg',name:'9'},
            {src:'/assets/images/10.jpeg',name:'10'},
            {src:'/assets/images/11.jpeg',name:'11'},
            {src:'/assets/images/12.jpeg',name:'12'}
        ]
    }
    handleClickEvent = query => {
        let images = this.state.images;
        if (this.state.items[query]) {
            this.setState({
                msg: "you guessed incorrectly",
                score: 0,
                items: {},
                images: images
            });
            return;
        }
        let items = this.state.items;
        items[query] = 1;
        this.setState({
            score: this.state.score + 1,
            topScore: this.state.topScore + 1,
            msg: "You guessed correctly",
            items: items,
            images:images
        });
        if (this.state.score === 12) {
            this.setState({
                msg: "you won, game over",
                score: 0,
                topScore:0,
                items: {},
                images: images
            });
        }
    }
    render() {
        return (
            <div>
                <NavBar
                    score={this.state.score}
                    topScore={this.state.topScore}
                    ms={this.state.msg}
                />
                <Header />
                <main className="container">
                    {
                        this.state.fish.map(fish => (
                    <Container
                        images = { this.state.images }
                        handleClickEvent = { this.handleClickEvent }
                    />
                 ))
                }
                </main>
                <Footer/>
            </div>
        )
    }
}
export default MainContainer;
