import React from 'react';
import './style.css';

function Container(props) {

    let createDives = () => {
        let Divs = [];
        let images = this.props.images;
        while(images.length != 0){
            let index = Math.floor(Math.random() * images.length);
            let image_obj = images[index];

            Divs.push(
                <div
                    role="img"
                    className="click-item"
                    name={`${ image_obj.name }`}
                    onClick={() => props.handleClickEvent(image_obj.name)}
                    style={{ backgroundImage: "url(" + image_obj.src + ")" }}> </div>);
            images.splice(index, 1);
        }
        return Divs;
    }
    return (
        <main class="container">
            this.createDives();
        </main>
    )
}
export default Container;
